mapper.select:
select from_unixtime(create_time/1000,'%Y-%m-%d') as dataStr,count(*) as num from tesseract_log where (create_time between #{startTime} and #{endTime} ) and status=0
and group_id=#{groupId}
group by from_unixtime(create_time/1000,'%Y-%m-%d')

select from_unixtime(create_time/1000,'%Y-%m-%d') as dataStr,count(*) as num from tesseract_log where (create_time between #{startTime} and #{endTime} ) and status=1
and group_id=#{groupId}
group by from_unixtime(create_time/1000,'%Y-%m-%d')

select
status,
count(*) as num
from tesseract_log

    where group_id=#{groupId}

group by status;
